import React from 'react';
import { TouchableOpacity, StyleSheet, Text } from "react-native";
import { backgroundColor } from 'react-native/Libraries/Components/View/ReactNativeStyleAttributes';
import colors from '../images/config/colors';

function AppButton({title, onPress, btnCcolor = colors.primary}) {
    return (
      <TouchableOpacity style={[styles.button, {backgroundColor: colors[btnCcolor]}]} onPress={onPress}>
        <Text style={styles.text}>{title}</Text>
      </TouchableOpacity>
    );
}

const styles = StyleSheet.create({
  button: {
    width: "100%",
    padding: 15,
    borderRadius: 25,
    justifyContent: "center",
    alignItems: "center",
    marginVertical  :10
  },
  text: {
    fontSize: 18,
    color: colors.white,
    textTransform: "uppercase",
    fontWeight: "bold",
  },
});
export default AppButton;
